import React, {Component, Fragment} from "react";
import ReactDOM from "react-dom";
import "./assets/style.css";
import quizService from "./quizService";
import QuestionBox from "./components/QuestionBox";
import Result from "./components/Result";
import Inicio from "./components/Inicio";
import Footer from "./components/Footer"



class CovidTest extends Component {


	state = {
		questionBank: [],
		score: 0,
		responses: 0,
		inicio: false,
		lat: null,
		lon: null,
		address: null
	}
	;



	
	//Iniciar el Test 
	showshow = () => {
    	this.setState(
			{inicio: true
			}
		);
		if(navigator.geolocation){
			navigator.geolocation.getCurrentPosition(this.getCoordinates);
		}
		else {
			alert("Tu navegador no soporta la Geolocalización");
		}
		console.log("hahaha");

	}


		
	//Obtener Coordenadas
	getCoordinates = (position, lat, lon) => {
	lat = position.coords.longitude;
	lon = position.coords.latitude;
	console.log(lat);
	console.log(lon);
	//this.setState(
		//{
		//	longitude: position.coords.longitude
	//}
	//);
}








	//Obtener las preguntas
	getQuestions = (inicio) => {
			quizService().then(
				question => {
					this.setState(
						{
							questionBank: question
						});
				});

	}

	//Computar la respuesta
	computeAnswer = (answer, puntaje) => {
		if (answer === "SI") {
			this.setState(
				{score: this.state.score + puntaje}
			);

		}

		this.setState(
			{
				responses: this.state.responses < 8 ? this.state.responses + 1 : 8
			}
		);

	}

	//Jugar de Nuevo
	playAgain = () => {
		this.getQuestions();
		this.setState({
			score: 0,
			responses: 0
		});
	}

	//Publicar los síntomas
	publicarSintomas = (valoracion, latitud) => {
			valoracion = this.state.score;
			latitud = this.state.lat;
			console.log(valoracion);
			alert("Valoración: " + latitud);
	}

	

	//Ciclo de vida
	componentDidMount(){
		this.getQuestions();
	}
	render() {
		return (
			<Fragment>
			<div className="content">
			<div className="title">AUTO-TEST COVID-19 (SARS-COV-2)</div>
			<div className="container">
				{this.state.inicio === false ? (<Inicio	
				inicio={this.state.inicio} showshow={this.showshow} getCoordinates={this.getCoordinates}	/>): null }
				{this.state.inicio === true && this.state.questionBank.length > 0 &&
				this.state.responses < 8 && 
				this.state.questionBank.map(({question, answers, puntaje, questionId}) => (
					<QuestionBox 
					question={question} 
					options={answers} 
					key={questionId}
					selected={answer => this.computeAnswer(answer, puntaje)}
					/>
				))}

				{this.state.responses === 8 ? (<Result score={this.state.score} playAgain={this.playAgain} publicarSintomas={this.publicarSintomas} lat={this.state.lat} />) : null}


			</div>

			
			<Footer/>


			</div>



			</Fragment>
			);
	}
}

ReactDOM.render( <CovidTest />, document.getElementById("root") );
