//Importamos React
import React, {Component} from "react";
//Importamos el DOM. Ya que es una web app
import ReactDOM from "react-dom";
//Importamos los Estilos
import "./assets/style.css";
//Importamos el API 
import quizService from "./quizService";
//Importamos el componente QuestionBox
import QuestionBox from "./components/QuestionBox";
//Importamos el componente donde se renderiza la respuesta
import Result from "./components/Result";


class CovidTest extends Component {
	state = {
		questionBank: [],
		score: 0,
		responses: 0
	};
	getQuestions = () => {
		quizService().then(
			question => {
				this.setState(
					{
						questionBank: question
					});
			});
	};

	computeAnswer = (answer, correctAnswer) => {
		if (answer === correctAnswer){
			this.setState(
				{score: this.state.score + 1}
			);
		}
		this.setState(
			{
				responses: this.state.responses < 5 ? this.state.responses + 1 : 5
			}
		);
	}

	componentDidMount(){
		this.getQuestions();
	}
	render() {
		return (
			<div className="container">
				<div className="title">Corona Test</div>
				{this.state.questionBank.length > 0 &&
				this.state.responses < 5 && 
				this.state.questionBank.map(({question, answers, correct, questionId}) => (
					<QuestionBox 
					question={question} 
					options={answers} 
					key={questionId}
					selected={answer => this.computeAnswer(answer, correct)}
					/>
				))}

				{this.state.responses === 5 ? (<Result score={this.state.score} playAgain={this.playAgain} />) : null}
			</div>
			);
	}
}

ReactDOM.render( <CovidTest />, document.getElementById("root") );
