//Importamos React
import React, {Component} from "react";
//Importamos el DOM. Ya que es una web app
import ReactDOM from "react-dom";
//Importamos los Estilos
import "./assets/style.css";
//Importamos el API 
import quizService from "./quizService";


class CovidTest extends Component {
	state = {
		questionBank: []
	};
	getQuestions = () => {
		quizService().then(
			question => {
				this.setState(
					{
						questionBank: question
					});
			});
	};
	componentDidMount(){
		this.getQuestions();
	}
	render() {
		return (
			<div className="container">
				<div className="title">Corona Test</div>
				{this.state.questionBank.length > 0 && this.state.questionBank.map(({question, answers, correct, questionId}) => (<h4>{question}</h4>))}
			</div>
			);
	}
}

ReactDOM.render( <CovidTest />, document.getElementById("root") );
