	}

	componentDidMount(){
		this.getQuestions();
	}
	render() {
		return (
			<div className="container">
				<div className="title">Corona Test</div>
				{this.state.questionBank.length > 0 && 
				this.state.questionBank.map(({question, answers, correct, questionId}) => (
					<QuestionBox 
					question={question} 
					options={answers} 
					key={questionId}
