//Importamos Re
import React, {Component} from "react";
import ReactDOM from "react-dom";
import "./assets/style.css";
import quizService from "./quizService";
import QuestionBox from "./components/QuestionBox";
import Result from "./components/Result";
import Warning from "./components/Warning";



class CovidTest extends Component {
	state = {
		questionBank: [],
		score: 0,
		responses: 0
	};
	getQuestions = () => {
		quizService().then(
			question => {
				this.setState(
					{
						questionBank: question
					});
			});
	};

	/*computeAnswer = (answer, puntaje) => {
		if (answer === puntaje){
			this.setState(
				{score: this.state.score}
			);
		}
		this.setState(
			{
				responses: this.state.responses < 8 ? this.state.responses + 1 : 8
			}
		);
	}*/

	computeAnswer = (answer, puntaje) => {
		if (answer === "SI") {
			//console.log(this.setState);
			this.setState(
				{score: this.state.score + puntaje}
			);
			console.log(puntaje);

		}

		this.setState(
			{
				responses: this.state.responses < 8 ? this.state.responses + 1:8
			}
		);

	}

	//Jugar de Nuevo
	playAgain = () => {
		this.getQuestions();
		this.setState({
			score: 0,
			responses: 0
		});
	}



	componentDidMount(){
		this.getQuestions();
	}
	render() {
		return (
			<div className="container">
				<div className="title">Haz tu autochequeo del COVID-19 </div>
				{this.state.questionBank.length > 0 &&
				this.state.responses < 8 && 
				this.state.questionBank.map(({question, answers, puntaje, questionId}) => (
					<QuestionBox 
					question={question} 
					options={answers} 
					key={questionId}
					selected={answer => this.computeAnswer(answer, puntaje)}
					/>
				))}

				{this.state.responses === 8 ? (<Result score={this.state.score} playAgain={this.playAgain} />, <Warning/>) : null}

			</div>
			);
	}
}

ReactDOM.render( <CovidTest />, document.getElementById("root") );
